package com.thinksns.sociax.unit;import com.thinksns.sociax.android.R;import android.app.Activity;import android.widget.EditText;public class AtHelper {	private Activity activity;	private String strDefaultAt = "";	private EditText editText;	public AtHelper(Activity paramEditorActivity, EditText editText) {		this.activity = paramEditorActivity;		this.editText = editText;		this.strDefaultAt = this.activity.getString(R.string.input_text_at);	}	public void insertAtTips() {		EditText localEditDiggView = this.editText;		int i = localEditDiggView.getSelectionStart();		int j = localEditDiggView.getSelectionEnd();		localEditDiggView.getText().replace(i, j, this.strDefaultAt + " ");		localEditDiggView.setSelection(i + 1, i + this.strDefaultAt.length());	}}